import React from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip } from 'recharts';

const RevenueChart = () => {
  const data = [
    { name: 'كشوفات', value: 45, color: '#3b82f6' },
    { name: 'تطعيمات', value: 30, color: '#10b981' },
    { name: 'جراحات', value: 20, color: '#f59e0b' },
    { name: 'أدوية', value: 5, color: '#ef4444' },
  ];

  const total = data.reduce((sum, item) => sum + item.value, 0);

  const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
      const data = payload[0];
      return (
        <div className="bg-white p-3 rounded-lg shadow-lg border border-gray-200">
          <p className="font-medium text-gray-900">{data.name}</p>
          <p style={{ color: data.payload.color }}>
            {`${data.value}%`}
          </p>
        </div>
      );
    }
    return null;
  };

  return (
    <div className="flex items-center justify-center">
      <div style={{ width: '100%', height: 300 }} className="relative">
        <ResponsiveContainer>
          <PieChart>
            <Pie
              data={data}
              cx="50%"
              cy="50%"
              innerRadius={60}
              outerRadius={100}
              paddingAngle={2}
              dataKey="value"
            >
              {data.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={entry.color} />
              ))}
            </Pie>
            <Tooltip content={<CustomTooltip />} />
          </PieChart>
        </ResponsiveContainer>
        
        {/* Center text */}
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="text-center">
            <div className="text-2xl font-bold text-gray-900">100%</div>
            <div className="text-sm text-gray-600">الإيرادات</div>
          </div>
        </div>
      </div>
      
      {/* Legend */}
      <div className="mr-8">
        {data.map((item, index) => (
          <div key={index} className="flex items-center mb-3">
            <div 
              className="w-3 h-3 rounded-full ml-2"
              style={{ backgroundColor: item.color }}
            ></div>
            <span className="text-sm text-gray-700">{item.name}</span>
            <span className="text-sm text-gray-500 mr-2">({item.value}%)</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RevenueChart;
