import React, { useState, useEffect } from 'react';
import { Plus, Search, Edit, Trash2, MapPin, Clock, Users } from 'lucide-react';

const Branches = () => {
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editingBranch, setEditingBranch] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    location: '',
    phone: '',
    email: '',
    manager: '',
    capacity: '',
    workingHours: {
      start: '',
      end: ''
    },
    workingDays: [],
    services: [],
    isActive: true
  });

  // جلب الفروع من API
  const fetchBranches = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/branches', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setBranches(data.branches || []);
      }
    } catch (error) {
      console.error('Error fetching branches:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBranches();
  }, []);

  // تصفية الفروع حسب البحث
  const filteredBranches = branches.filter(branch =>
    branch.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    branch.location?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    branch.manager?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // فتح نموذج إضافة/تعديل
  const openModal = (branch = null) => {
    setEditingBranch(branch);
    setFormData(branch ? {
      name: branch.name || '',
      location: branch.location || '',
      phone: branch.phone || '',
      email: branch.email || '',
      manager: branch.manager || '',
      capacity: branch.capacity || '',
      workingHours: {
        start: branch.workingHours?.start || '',
        end: branch.workingHours?.end || ''
      },
      workingDays: branch.workingDays || [],
      services: branch.services || [],
      isActive: branch.isActive !== undefined ? branch.isActive : true
    } : {
      name: '',
      location: '',
      phone: '',
      email: '',
      manager: '',
      capacity: '',
      workingHours: {
        start: '',
        end: ''
      },
      workingDays: [],
      services: [],
      isActive: true
    });
    setShowModal(true);
  };

  // حفظ الفرع
  const handleSave = async (e) => {
    e.preventDefault();
    
    try {
      const token = localStorage.getItem('token');
      const url = editingBranch 
        ? `/api/branches/${editingBranch._id}`
        : '/api/branches';
      
      const method = editingBranch ? 'PUT' : 'POST';
      
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        setShowModal(false);
        fetchBranches();
        alert(editingBranch ? 'تم تحديث الفرع بنجاح' : 'تم إضافة الفرع بنجاح');
      }
    } catch (error) {
      console.error('Error saving branch:', error);
      alert('حدث خطأ أثناء الحفظ');
    }
  };

  // حذف الفرع
  const handleDelete = async (branchId) => {
    if (!window.confirm('هل أنت متأكد من حذف هذا الفرع؟')) return;

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`/api/branches/${branchId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        fetchBranches();
        alert('تم حذف الفرع بنجاح');
      }
    } catch (error) {
      console.error('Error deleting branch:', error);
      alert('حدث خطأ أثناء الحذف');
    }
  };

  // تبديل حالة الفرع
  const toggleBranchStatus = async (branchId, isActive) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`/api/branches/${branchId}/status`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ isActive: !isActive })
      });

      if (response.ok) {
        fetchBranches();
        alert(`تم ${!isActive ? 'تفعيل' : 'إيقاف'} الفرع بنجاح`);
      }
    } catch (error) {
      console.error('Error toggling branch status:', error);
      alert('حدث خطأ أثناء تحديث الحالة');
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setFormData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else if (type === 'checkbox') {
      setFormData(prev => ({
        ...prev,
        [name]: checked
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleArrayChange = (field, value) => {
    const currentArray = formData[field];
    const newArray = currentArray.includes(value)
      ? currentArray.filter(item => item !== value)
      : [...currentArray, value];
    
    setFormData(prev => ({
      ...prev,
      [field]: newArray
    }));
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="p-6">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">إدارة الفروع</h1>
          <p className="text-gray-600">إدارة جميع فروع العيادة</p>
        </div>
        <button
          onClick={() => openModal()}
          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg flex items-center gap-2"
        >
          <Plus className="h-5 w-4" />
          إضافة فرع جديد
        </button>
      </div>

      {/* Search */}
      <div className="mb-6">
        <div className="relative">
          <Search className="absolute right-3 top-3 h-5 w-5 text-gray-400" />
          <input
            type="text"
            placeholder="البحث عن فرع (الاسم، الموقع، المدير)"
            className="w-full pr-10 pl-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <div className="p-2 bg-blue-100 rounded-lg">
              <MapPin className="h-6 w-6 text-blue-600" />
            </div>
            <div className="mr-4">
              <h3 className="text-lg font-semibold text-gray-900">{branches.length}</h3>
              <p className="text-gray-600">إجمالي </p>
            </div>
          </div>
        </div>
        
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <div className="p-2 bg-green-100 rounded-lg">
              <MapPin className="h-6 w-6 text-green-600" />
            </div>
            <div className="mr-4">
              <h3 className="text-lg font-semibold text-gray-900">
                {branches.filter(b => b.isActive).length}
              </h3>
              <p className="text-gray-600">الفروع النشطة</p>
            </div>
          </div>
        </div>
        
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <div className="p-2 bg-yellow-100 rounded-lg">
              <Users className="h-6 w-6 text-yellow-600" />
            </div>
            <div className="mr-4">
              <h3 className="text-lg font-semibold text-gray-900">
                {branches.reduce((total, branch) => total + (branch.capacity || 0), 0)}
              </h3>
              <p className="text-gray-600">إجمالي السعة</p>
            </div>
          </div>
        </div>
      </div>

      {/* Branches Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredBranches.map((branch) => (
          <div key={branch._id} className="bg-white rounded-lg shadow hover:shadow-lg transition-shadow">
            <div className="p-6">
              {/* Branch Header */}
              <div className="flex justify-between items-start mb-4">
                <div>
                  <h3 className="text-lg font-semibold text-gray-900">{branch.name}</h3>
                  <div className="flex items-center text-gray-600 mt-1">
                    <MapPin className="h-4 w-4 ml-1" />
                    <span className="text-sm">{branch.location}</span>
                  </div>
                </div>
                <div className="flex items-center">
                  <div className={`w-3 h-3 rounded-full ${branch.isActive ? 'bg-green-500' : 'bg-red-500'}`}></div>
                  <span className={`text-xs ml-2 ${branch.isActive ? 'text-green-600' : 'text-red-600'}`}>
                    {branch.isActive ? 'نشط' : 'غير نشط'}
                  </span>
                </div>
              </div>

              {/* Branch Details */}
              <div className="space-y-3">
                {branch.manager && (
                  <div className="flex items-center text-sm text-gray-600">
                    <Users className="h-4 w-4 ml-2" />
                    المدير: {branch.manager}
                  </div>
                )}
                
                {branch.phone && (
                  <div className="flex items-center text-sm text-gray-600">
                    <Clock className="h-4 w-4 ml-2" />
                    {branch.phone}
                  </div>
                )}
                
                {branch.workingHours?.start && branch.workingHours?.end && (
                  <div className="flex items-center text-sm text-gray-600">
                    <Clock className="h-4 w-4 ml-2" />
                    {branch.workingHours.start} - {branch.workingHours.end}
                  </div>
                )}
                
                {branch.capacity && (
                  <div className="text-sm text-gray-600">
                    السعة: {branch.capacity} عميل/يوم
                  </div>
                )}
              </div>

              {/* Services */}
              {branch.services && branch.services.length > 0 && (
                <div className="mt-4">
                  <div className="text-sm text-gray-600 mb-2">الخدمات:</div>
                  <div className="flex flex-wrap gap-1">
                    {branch.services.slice(0, 3).map((service, index) => (
                      <span
                        key={index}
                        className="inline-block bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded"
                      >
                        {service}
                      </span>
                    ))}
                    {branch.services.length > 3 && (
                      <span className="inline-block bg-gray-100 text-gray-600 text-xs px-2 py-1 rounded">
                        +{branch.services.length - 3}
                      </span>
                    )}
                  </div>
                </div>
              )}

              {/* Actions */}
              <div className="flex justify-between items-center mt-6 pt-4 border-t border-gray-200">
                <div className="flex space-x-2">
                  <button
                    onClick={() => openModal(branch)}
                    className="text-blue-600 hover:text-blue-800"
                    title="تعديل"
                  >
                    <Edit className="h-4 w-4" />
                  </button>
                  <button
                    onClick={() => handleDelete(branch._id)}
                    className="text-red-600 hover:text-red-800"
                    title="حذف"
                  >
                    <Trash2 className="h-4 w-4" />
                  </button>
                </div>
                
                <button
                  onClick={() => toggleBranchStatus(branch._id, branch.isActive)}
                  className={`px-3 py-1 text-xs rounded-full ${
                    branch.isActive 
                      ? 'bg-red-100 text-red-700 hover:bg-red-200' 
                      : 'bg-green-100 text-green-700 hover:bg-green-200'
                  }`}
                >
                  {branch.isActive ? 'إيقاف' : 'تفعيل'}
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {filteredBranches.length === 0 && (
        <div className="text-center py-12">
          <MapPin className="mx-auto h-12 w-12 text-gray-400" />
          <h3 className="mt-2 text-sm font-medium text-gray-900">لا توجد فروع</h3>
          <p className="mt-1 text-sm text-gray-500">ابدأ بإضافة فرع جديد</p>
        </div>
      )}

      {/* Add/Edit Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center">
          <div className="bg-white p-8 rounded-lg shadow-xl max-w-2xl w-full mx-4 max-h-screen overflow-y-auto">
            <h3 className="text-lg font-medium text-gray-900 mb-4">
              {editingBranch ? 'تعديل الفرع' : 'إضافة فرع جديد'}
            </h3>
            
            <form onSubmit={handleSave} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    اسم الفرع
                  </label>
                  <input
                    type="text"
                    name="name"
                    required
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    value={formData.name}
                    onChange={handleChange}
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    الموقع
                  </label>
                  <input
                    type="text"
                    name="location"
                    required
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    value={formData.location}
                    onChange={handleChange}
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    رقم الهاتف
                  </label>
                  <input
                    type="tel"
                    name="phone"
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    value={formData.phone}
                    onChange={handleChange}
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    البريد الإلكتروني
                  </label>
                  <input
                    type="email"
                    name="email"
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    value={formData.email}
                    onChange={handleChange}
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    المدير
                  </label>
                  <input
                    type="text"
                    name="manager"
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    value={formData.manager}
                    onChange={handleChange}
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    السعة (عميل/يوم)
                  </label>
                  <input
                    type="number"
                    name="capacity"
                    min="1"
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    value={formData.capacity}
                    onChange={handleChange}
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    بداية العمل
                  </label>
                  <input
                    type="time"
                    name="workingHours.start"
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    value={formData.workingHours.start}
                    onChange={handleChange}
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    نهاية العمل
                  </label>
                  <input
                    type="time"
                    name="workingHours.end"
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    value={formData.workingHours.end}
                    onChange={handleChange}
                  />
                </div>
              </div>

              {/* Working Days */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  أيام العمل
                </label>
                <div className="grid grid-cols-4 gap-2">
                  {['الأحد', 'الاثنين', 'الثلاثاء', 'الأربعاء', 'الخميس', 'الجمعة', 'السبت'].map((day) => (
                    <label key={day} className="flex items-center">
                      <input
                        type="checkbox"
                        checked={formData.workingDays.includes(day)}
                        onChange={() => handleArrayChange('workingDays', day)}
                        className="ml-2"
                      />
                      <span className="text-sm">{day}</span>
                    </label>
                  ))}
                </div>
              </div>

              {/* Services */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  الخدمات المتاحة
                </label>
                <div className="grid grid-cols-2 gap-2">
                  {['تطعيمات الإبل', 'تطعيمات الأغنام', 'تطعيمات الماعز', 'تطعيمات الماشية', 'تطعيمات الخيول', 'استشارات طبية', 'فحوصات طبية', 'جراحات بسيطة'].map((service) => (
                    <label key={service} className="flex items-center">
                      <input
                        type="checkbox"
                        checked={formData.services.includes(service)}
                        onChange={() => handleArrayChange('services', service)}
                        className="ml-2"
                      />
                      <span className="text-sm">{service}</span>
                    </label>
                  ))}
                </div>
              </div>

              {/* Status */}
              <div className="flex items-center">
                <input
                  type="checkbox"
                  name="isActive"
                  id="isActive"
                  checked={formData.isActive}
                  onChange={handleChange}
                  className="ml-2"
                />
                <label htmlFor="isActive" className="text-sm font-medium text-gray-700">
                  الفرع نشط
                </label>
              </div>

              <div className="flex justify-end space-x-3 pt-4">
                <button
                  type="button"
                  onClick={() => setShowModal(false)}
                  className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50"
                >
                  إلغاء
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
                >
                  {editingBranch ? 'تحديث' : 'إضافة'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Branches;
