import React, { useState, useEffect, useCallback } from 'react';
import { Plus, Search, Edit, Trash2, Calendar, Clock, User, File ,CircleCheckBig, Bell, X} from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';
import CalendarSlider from '../components/CalendarSlider';

const Bookings = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [showAllBookings, setShowAllBookings] = useState(false); // ÿ≤ÿ± ŸÑÿπÿ±ÿ∂ ŸÉŸÑ ÿßŸÑÿ≠ÿ¨Ÿàÿ≤ÿßÿ™
  const [bookings, setBookings] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [vaccinations, setVaccinations] = useState([]);
  const [branches, setBranches] = useState([]);
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [showModal, setShowModal] = useState(false);
  const [editingBooking, setEditingBooking] = useState(null);
  const [formData, setFormData] = useState({
    customerId: '',
    animalId: '',
    animalName: '',
    animalType: '',
    vaccinationId: '',
    branchId: '',
    appointmentDate: '',
    timeSlot: '',
    notes: ''
  });

  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000';

  const authorizedFetch = (endpoint, options = {}) => {
    const token = localStorage.getItem('token');
    const headers = {
      ...(options.headers || {}),
    };

    if (token) {
      headers.Authorization = `Bearer ${token}`;
    }

    return fetch(`${API_BASE_URL}${endpoint}`, {
      ...options,
      headers,
    });
  };

  // ÿ¨ŸÑÿ® ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÖŸÜ API
  const fetchData = useCallback(async () => {
    try {
      let bookingsEndpoint = '/api/bookings';
      
      // ÿ•ÿ∞ÿß ŸÑŸÖ ŸÜŸÉŸÜ ŸÅŸä Ÿàÿ∂ÿπ "ÿπÿ±ÿ∂ ÿßŸÑŸÉŸÑ"ÿå ÿ£ÿ∂ŸÅ ÿ™ÿµŸÅŸäÿ© ÿßŸÑÿ¥Ÿáÿ±
      if (!showAllBookings) {
        const month = selectedDate.getMonth(); // 0-11
        const year = selectedDate.getFullYear();
        bookingsEndpoint = `/api/bookings?month=${month}&year=${year}`;
        console.log('Fetching bookings for:', { month, year, selectedDate });
      } else {
        console.log('Fetching all bookings (no date filter)');
      }
      
      const [bookingsRes, customersRes, vaccinationsRes, branchesRes] = await Promise.all([
        authorizedFetch(bookingsEndpoint),
        authorizedFetch('/api/customers'),
        authorizedFetch('/api/vaccinations'),
        authorizedFetch('/api/branches')
      ]);

      if (bookingsRes.ok) {
        const bookingsData = await bookingsRes.json();
        console.log('‚úÖ Bookings API Response:', bookingsData);
        console.log('üìä Bookings array:', bookingsData.data?.bookings);
        console.log('üî¢ Number of bookings:', bookingsData.data?.bookings?.length || 0);
        
        const bookingsList = bookingsData.data?.bookings || [];
        console.log('üíæ Setting bookings state with:', bookingsList.length, 'items');
        setBookings(bookingsList);
        
        // Force re-render after a moment to ensure state is updated
        setTimeout(() => {
          console.log('üîÑ Current bookings state:', bookingsList.length);
        }, 100);
      } else {
        console.error('‚ùå Failed to fetch bookings:', bookingsRes.status);
        const errorData = await bookingsRes.json();
        console.error('Error details:', errorData);
      }

      if (customersRes.ok) {
        const customersData = await customersRes.json();
        console.log('Customers data:', customersData);
        setCustomers(customersData.data || customersData.data?.customers || []);
      }

      if (vaccinationsRes.ok) {
        const vaccinationsData = await vaccinationsRes.json();
        console.log('Vaccinations data:', vaccinationsData);
        const vaccinationsList = vaccinationsData.data?.vaccinations || vaccinationsData.data || [];
        console.log('Vaccinations list:', vaccinationsList);
        setVaccinations(vaccinationsList);
      } else {
        console.error('Failed to fetch vaccinations:', vaccinationsRes.status);
      }

      if (branchesRes.ok) {
        const branchesData = await branchesRes.json();
        console.log('Branches data:', branchesData);
        setBranches(branchesData.data || branchesData.data?.branches || []);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  }, [selectedDate, showAllBookings]); // Dependencies

  useEffect(() => {
    fetchData();
  }, [fetchData]); // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ fetchData ŸÉŸÄ dependency

  // ÿ™ÿµŸÅŸäÿ© ÿßŸÑÿ≠ÿ¨Ÿàÿ≤ÿßÿ™
  const filteredBookings = bookings.filter(booking => {
    const matchesSearch =
      booking.customer?.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      booking.customer?.phone?.includes(searchTerm) ||
      booking.animal?.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      booking.bookingNumber?.includes(searchTerm);

    const matchesStatus = statusFilter === 'all' || booking.status === statusFilter;

    return matchesSearch && matchesStatus;
  });

  console.log('üîç Filter status:', {
    totalBookings: bookings.length,
    filteredBookings: filteredBookings.length,
    searchTerm,
    statusFilter
  });

  // ŸÅÿ™ÿ≠ ŸÜŸÖŸàÿ∞ÿ¨ ÿ•ÿ∂ÿßŸÅÿ©/ÿ™ÿπÿØŸäŸÑ
  const openModal = (booking = null) => {
    setEditingBooking(booking);
    
    if (booking) {
      // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ŸÅŸä Ÿàÿ∂ÿπ ÿßŸÑÿ™ÿπÿØŸäŸÑÿå ÿßÿ®ÿ≠ÿ´ ÿπŸÜ ÿßŸÑÿπŸÖŸäŸÑ Ÿàÿ≠ÿØÿØŸá
      const customer = customers.find(c => c._id === booking.customer?._id);
      setSelectedCustomer(customer || null);
      
      setFormData({
        customerId: booking.customer?._id || '',
        animalId: booking.animal?._id || '',
        animalName: booking.animal?.name || '',
        animalType: booking.animal?.type || '',
        vaccinationId: booking.vaccination?._id || booking.vaccination?.id || '',
        branchId: booking.branch?._id || '',
        appointmentDate: booking.appointmentDate ? new Date(booking.appointmentDate).toISOString().split('T')[0] : '',
        timeSlot: booking.appointmentTime || booking.timeSlot || '',
        notes: booking.notes || ''
      });
    } else {
      // Ÿàÿ∂ÿπ ÿßŸÑÿ•ÿ∂ÿßŸÅÿ©
      setSelectedCustomer(null);
      setFormData({
        customerId: '',
        animalId: '',
        animalName: '',
        animalType: '',
        vaccinationId: '',
        branchId: '',
        appointmentDate: '',
        timeSlot: '',
        notes: ''
      });
    }
    
    setShowModal(true);
  };

  // ÿ≠ŸÅÿ∏ ÿßŸÑÿ≠ÿ¨ÿ≤
  const handleSave = async (e) => {
    e.preventDefault();
    
    try {
      const url = editingBooking 
        ? `${API_BASE_URL}/api/bookings/${editingBooking._id}`
        : `${API_BASE_URL}/api/bookings`;
      
      const method = editingBooking ? 'PUT' : 'POST';
      
      // ÿßŸÑÿ®ÿ≠ÿ´ ÿπŸÜ ÿßŸÑÿ™ÿ∑ÿπŸäŸÖ ÿßŸÑŸÖÿ≠ÿØÿØ ŸÑŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ŸÖÿπŸÑŸàŸÖÿßÿ™Ÿá
      const selectedVaccination = vaccinations.find(v => v._id === formData.vaccinationId);
      
      if (!selectedVaccination) {
        alert('ÿßŸÑÿ±ÿ¨ÿßÿ° ÿßÿÆÿ™Ÿäÿßÿ± ÿßŸÑÿ™ÿ∑ÿπŸäŸÖ');
        return;
      }
      
      const bookingData = {
        customer: formData.customerId,
        animal: {
          name: formData.animalName,
          type: formData.animalType,
          age: 0,
          weight: 0
        },
        vaccination: {
          id: selectedVaccination._id,
          type: selectedVaccination.name || selectedVaccination.nameAr,
          name: selectedVaccination.nameAr || selectedVaccination.name,
          nameAr: selectedVaccination.nameAr,
          price: selectedVaccination.price,
          duration: selectedVaccination.duration,
          dosage: '1ml',
          manufacturer: 'Veterinary Pharmaceuticals',
          batchNumber: `B${Math.floor(Math.random() * 10000)}`,
          expiryDate: new Date(Date.now() + 365 * 24 * 60 * 60 * 1000).toISOString()
        },
        branch: formData.branchId,
        appointmentDate: formData.appointmentDate,
        appointmentTime: formData.timeSlot,
        price: selectedVaccination.price,
        paymentMethod: 'cash',
        notes: formData.notes || ''
      };
      
      console.log('Sending booking data:', bookingData);
      
      const response = await authorizedFetch(url.replace(API_BASE_URL, ''), {
        method,
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(bookingData)
      });

      const data = await response.json();
      console.log('Response:', data);

      if (response.ok) {
        setShowModal(false);
        setSelectedCustomer(null);
        fetchData();
        alert(editingBooking ? 'ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ≠ÿ¨ÿ≤ ÿ®ŸÜÿ¨ÿßÿ≠' : 'ÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ≠ÿ¨ÿ≤ ÿ®ŸÜÿ¨ÿßÿ≠');
      } else {
        let errorMessage = 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ≠ŸÅÿ∏';
        if (data.errors && data.errors.length > 0) {
          errorMessage = data.errors.map(err => `${err.field}: ${err.message}`).join('\n');
        } else if (data.message) {
          errorMessage = data.message;
        }
        alert(errorMessage);
      }
    } catch (error) {
      console.error('Error saving booking:', error);
      alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ≠ŸÅÿ∏: ' + error.message);
    }
  };

  // ÿ≠ÿ∞ŸÅ ÿßŸÑÿ≠ÿ¨ÿ≤
  const handleDelete = async (bookingId) => {
    if (!window.confirm('ŸáŸÑ ÿ£ŸÜÿ™ ŸÖÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ≠ÿ∞ŸÅ Ÿáÿ∞ÿß ÿßŸÑÿ≠ÿ¨ÿ≤ÿü')) return;

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`/api/bookings/${bookingId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        fetchData();
        alert('ÿ™ŸÖ ÿ≠ÿ∞ŸÅ ÿßŸÑÿ≠ÿ¨ÿ≤ ÿ®ŸÜÿ¨ÿßÿ≠');
      }
    } catch (error) {
      console.error('Error deleting booking:', error);
      alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ≠ÿ∞ŸÅ');
    }
  };

  // ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ≠ÿ¨ÿ≤
  const handleStatusUpdate = async (bookingId, newStatus) => {
    try {
      const response = await authorizedFetch(`/api/bookings/${bookingId}/status`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ status: newStatus })
      });

      if (response.ok) {
        fetchData();
        const statusText = {
          'pending': 'ŸÅŸä ÿßŸÑÿßŸÜÿ™ÿ∏ÿßÿ±',
          'confirmed': 'ŸÖÿ§ŸÉÿØ',
          'completed': 'ŸÖŸÉÿ™ŸÖŸÑ',
          'cancelled': 'ŸÖŸÑÿ∫Ÿâ'
        };
        alert(`ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ≠ÿ¨ÿ≤ ÿ•ŸÑŸâ: ${statusText[newStatus]}`);
      } else {
        const error = await response.json();
        alert(error.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ™ÿ≠ÿØŸäÿ´');
      }
    } catch (error) {
      console.error('Error updating status:', error);
      alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ≠ÿßŸÑÿ©');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    // ÿπŸÜÿØ ÿßÿÆÿ™Ÿäÿßÿ± ÿπŸÖŸäŸÑÿå ŸÜÿ¨ŸÑÿ® ÿ®ŸäÿßŸÜÿßÿ™Ÿá Ÿàÿ≠ŸäŸàÿßŸÜÿßÿ™Ÿá
    if (name === 'customerId') {
      const customer = customers.find(c => c._id === value);
      setSelectedCustomer(customer);
      setFormData({
        ...formData,
        customerId: value,
        animalId: '',
        animalName: '',
        animalType: ''
      });
    }
    // ÿπŸÜÿØ ÿßÿÆÿ™Ÿäÿßÿ± ÿ≠ŸäŸàÿßŸÜ ŸÖŸÜ ÿ≠ŸäŸàÿßŸÜÿßÿ™ ÿßŸÑÿπŸÖŸäŸÑ
    else if (name === 'animalId' && selectedCustomer) {
      const animal = selectedCustomer.animals.find(a => a._id === value);
      setFormData({
        ...formData,
        animalId: value,
        animalName: animal?.name || '',
        animalType: animal?.type || ''
      });
    }
    else {
      setFormData({
        ...formData,
        [name]: value
      });
    }
  };

    const handleDateChange = (newDate) => {
    setSelectedDate(newDate);
  };

  // ÿØÿßŸÑÿ© ŸÑÿ™ÿ≠ÿØŸäÿØ ŸÑŸàŸÜ ÿßŸÑÿ≠ÿßŸÑÿ©
  const getStatusColor = (status) => {
    switch (status) {
      case 'confirmed': return 'bg-green-100 text-green-800';
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'completed': return 'bg-blue-100 text-blue-800';
      case 'cancelled': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  // ÿØÿßŸÑÿ© ŸÑÿ™ÿ≠ŸàŸäŸÑ ÿßŸÑÿ≠ÿßŸÑÿ© ÿ•ŸÑŸâ ÿßŸÑÿπÿ±ÿ®Ÿäÿ©
  const getStatusText = (status) => {
    switch (status) {
      case 'confirmed': return 'ŸÖÿ§ŸÉÿØ';
      case 'pending': return 'ŸÅŸä ÿßŸÑÿßŸÜÿ™ÿ∏ÿßÿ±';
      case 'completed': return 'ŸÖŸÉÿ™ŸÖŸÑ';
      case 'cancelled': return 'ŸÖŸÑÿ∫Ÿä';
      default: return status;
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="p-6 mt-14">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ≠ÿ¨Ÿàÿ≤ÿßÿ™</h1>
          <p className="text-gray-600">ÿ•ÿØÿßÿ±ÿ© ÿ¨ŸÖŸäÿπ ÿ≠ÿ¨Ÿàÿ≤ÿßÿ™ ÿßŸÑÿ™ÿ∑ÿπŸäŸÖÿßÿ™</p>
        </div>
        
       <div className='flex me-2 gap-5'>
         <CalendarSlider 
            className="w-auto" 
            value={selectedDate}
            onChange={handleDateChange}
          /> 
         <button
          onClick={() => openModal()}
          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg flex items-center gap-2"
        >
          
          <Plus className="h-5 w-5" />
          ÿ≠ÿ¨ÿ≤ ÿ¨ÿØŸäÿØ
        </button>
       </div>
        
      </div>

      {/* Filters */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        <div className="relative">
          <Search className="absolute right-3 top-3 h-5 w-5 text-gray-400" />
          <input
            type="text"
            placeholder="ÿßŸÑÿ®ÿ≠ÿ´ ÿπŸÜ ÿ≠ÿ¨ÿ≤ (ÿßŸÑÿßÿ≥ŸÖÿå ÿßŸÑŸáÿßÿ™ŸÅÿå ÿ±ŸÇŸÖ ÿßŸÑÿ≠ÿ¨ÿ≤)"
            className="w-full pr-10 pl-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        
        <select
          value={statusFilter}
          onChange={(e) => setStatusFilter(e.target.value)}
          className="border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
        >
          <option value="all">ÿ¨ŸÖŸäÿπ ÿßŸÑÿ≠ÿßŸÑÿßÿ™</option>
          <option value="pending">ŸÅŸä ÿßŸÑÿßŸÜÿ™ÿ∏ÿßÿ±</option>
          <option value="confirmed">ŸÖÿ§ŸÉÿØ</option>
          <option value="completed">ŸÖŸÉÿ™ŸÖŸÑ</option>
          <option value="cancelled">ŸÖŸÑÿ∫Ÿä</option>
        </select>
        
        <button
          onClick={() => setShowAllBookings(!showAllBookings)}
          className={`px-4 py-2 rounded-lg border transition-colors ${
            showAllBookings 
              ? 'bg-blue-600 text-white border-blue-600 hover:bg-blue-700' 
              : 'bg-white text-gray-700 border-gray-300 hover:bg-gray-50'
          }`}
        >
          {showAllBookings ? 'üìÖ ÿπÿ±ÿ∂ ÿ≠ÿ¨Ÿàÿ≤ÿßÿ™ ÿßŸÑÿ¥Ÿáÿ±' : 'üìã ÿπÿ±ÿ∂ ŸÉŸÑ ÿßŸÑÿ≠ÿ¨Ÿàÿ≤ÿßÿ™'}
        </button>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <div className="p-2 bg-blue-100 rounded-lg">
              <File className="h-6 w-6 text-blue-600" />
            </div>
            <div className="mr-4">
             
              <p className="text-gray-600">ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑÿ≠ÿ¨Ÿàÿ≤ÿßÿ™</p>
               <h3 className="text-lg font-semibold text-gray-900">{bookings.length}</h3>
            </div>
          </div>
        </div>
        
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <div className="p-2 bg-green-100 rounded-lg">
              <CircleCheckBig className="h-6 w-6 text-white-600" />
            </div>
            <div className="mr-4">
              
              <p className="text-gray-600">ÿßŸÑÿ≠ÿ¨Ÿàÿ≤ÿßÿ™ ÿßŸÑŸÖÿ§ŸÉÿØÿ©</p>
              <h3 className="text-lg font-semibold text-gray-900">
                {bookings.filter(b => b.status === 'confirmed').length}
              </h3>
            </div>
          </div>
        </div>
        
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <div className="p-2 bg-yellow-100 rounded-lg">
              <Bell className="h-6 w-6 text-yellow-600" />
            </div>
            <div className="mr-4">
              
              <p className="text-gray-600">ÿßŸÑÿ≠ÿ¨Ÿàÿ≤ÿßÿ™ ÿßŸÑŸÖÿπŸÑŸÇÿ©</p>
              <h3 className="text-lg font-semibold text-gray-900">
                {bookings.filter(b => b.status === 'pending').length}
              </h3>
            </div>
          </div>
        </div>
        
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <div className="p-2 bg-red-100 rounded-lg">
              <X className="h-6 w-6 text-red-600" />
            </div>
            <div className="mr-4">
              
              <p className="text-gray-600">ÿßŸÑÿ≠ÿ¨Ÿàÿ≤ÿßÿ™ ÿßŸÑŸÖŸÑÿ∫ÿßÿ©</p>
              <h3 className="text-lg font-semibold text-gray-900">
                {bookings.filter(b => b.status === 'cancelled').length}
              </h3>
            </div>
          </div>
        </div>
      </div>

      {/* Bookings Table */}
      <div className="bg-white rounded-lg shadow overflow-hidden">
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                  ÿ±ŸÇŸÖ ÿßŸÑÿ≠ÿ¨ÿ≤
                </th>
                <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                  ÿßŸÑÿπŸÖŸäŸÑ
                </th>
                <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                  ÿßŸÑÿ≠ŸäŸàÿßŸÜ
                </th>
                <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                  ÿßŸÑÿ™ÿ∑ÿπŸäŸÖ
                </th>
                <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                  ÿßŸÑÿ™ÿßÿ±ŸäÿÆ ŸàÿßŸÑŸàŸÇÿ™
                </th>
                <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                  ÿßŸÑÿ≠ÿßŸÑÿ©
                </th>
                <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                  ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {Array.isArray(filteredBookings) && filteredBookings.map((booking) => (
                <tr key={booking._id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-blue-600">
                    {booking.bookingNumber}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      <div className="h-8 w-8 bg-blue-100 rounded-full flex items-center justify-center">
                        <User className="h-4 w-4 text-blue-600" />
                      </div>
                      <div className="mr-3">
                        <div className="text-sm font-medium text-gray-900">
                          {booking.customer?.name}
                        </div>
                        <div className="text-sm text-gray-500">
                          {booking.customer?.phone}
                        </div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm font-medium text-gray-900">{booking.animal?.name}</div>
                    <div className="text-sm text-gray-500">{booking.animal?.type}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-900">{booking.vaccination?.name}</div>
                    <div className="text-sm text-gray-500">{booking.vaccination?.price} ÿ±ŸäÿßŸÑ</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center text-sm text-gray-900">
                      <Calendar className="h-4 w-4 ml-2" />
                      {new Date(booking.appointmentDate).toLocaleDateString('ar-SA')}
                    </div>
                    <div className="flex items-center text-sm text-gray-500">
                      <Clock className="h-4 w-4 ml-2" />
                      {booking.appointmentTime}
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getStatusColor(booking.status)}`}>
                      {getStatusText(booking.status)}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    <div className="flex space-x-2 gap-2">
                      {/* Dropdown ŸÑÿ™ÿ∫ŸäŸäÿ± ÿßŸÑÿ≠ÿßŸÑÿ© */}
                      <select
                        value={booking.status}
                        onChange={(e) => handleStatusUpdate(booking._id, e.target.value)}
                        className="text-sm border border-gray-300 rounded px-2 py-1 focus:outline-none focus:ring-2 focus:ring-blue-500"
                      >
                        <option value="pending">ŸÅŸä ÿßŸÑÿßŸÜÿ™ÿ∏ÿßÿ±</option>
                        <option value="confirmed">ŸÖÿ§ŸÉÿØ</option>
                        <option value="completed">ŸÖŸÉÿ™ŸÖŸÑ</option>
                        <option value="cancelled">ŸÖŸÑÿ∫Ÿâ</option>
                      </select>
                      
                      <button
                        onClick={() => openModal(booking)}
                        className="text-blue-600 hover:text-blue-900"
                        title="ÿ™ÿπÿØŸäŸÑ"
                      >
                        <Edit className="h-4 w-4" />
                      </button>
                      <button
                        onClick={() => handleDelete(booking._id)}
                        className="text-red-600 hover:text-red-900"
                      >
                        <Trash2 className="h-4 w-4" />
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {filteredBookings.length === 0 && (
            <div className="text-center py-12">
              <Calendar className="mx-auto h-12 w-12 text-gray-400" />
              <h3 className="mt-2 text-sm font-medium text-gray-900">ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ≠ÿ¨Ÿàÿ≤ÿßÿ™</h3>
              <p className="mt-1 text-sm text-gray-500">ÿßÿ®ÿØÿ£ ÿ®ÿ•ÿ∂ÿßŸÅÿ© ÿ≠ÿ¨ÿ≤ ÿ¨ÿØŸäÿØ</p>
            </div>
          )}
        </div>
      </div>

      {/* Add/Edit Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center">
          <div className="bg-white p-8 rounded-lg shadow-xl max-w-2xl w-full mx-4 max-h-screen overflow-y-auto">
            <h3 className="text-lg font-medium text-gray-900 mb-4">
              {editingBooking ? 'ÿ™ÿπÿØŸäŸÑ ÿßŸÑÿ≠ÿ¨ÿ≤' : 'ÿ•ÿ∂ÿßŸÅÿ© ÿ≠ÿ¨ÿ≤ ÿ¨ÿØŸäÿØ'}
            </h3>
            
            <form onSubmit={handleSave} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="md:col-span-2">
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    ÿßŸÑÿπŸÖŸäŸÑ *
                  </label>
                  <select
                    name="customerId"
                    required
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    value={formData.customerId}
                    onChange={handleChange}
                  >
                    <option value="">ÿßÿÆÿ™ÿ± ÿßŸÑÿπŸÖŸäŸÑ</option>
                    {Array.isArray(customers) && customers.map(customer => (
                      <option key={customer._id} value={customer._id}>
                        {customer.name} - {customer.phone}
                      </option>
                    ))}
                  </select>
                  {Array.isArray(customers) && customers.length === 0 && (
                    <p className="text-xs text-red-500 mt-1">ŸÑÿß ŸäŸàÿ¨ÿØ ÿπŸÖŸÑÿßÿ°! ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ•ÿ∂ÿßŸÅÿ© ÿπŸÖŸÑÿßÿ° ÿ£ŸàŸÑÿßŸã</p>
                  )}
                </div>

                {selectedCustomer && selectedCustomer.animals && selectedCustomer.animals.length > 0 ? (
                  <div className="md:col-span-2">
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      ÿßŸÑÿ≠ŸäŸàÿßŸÜ (ŸÖŸÜ ÿ≠ŸäŸàÿßŸÜÿßÿ™ ÿßŸÑÿπŸÖŸäŸÑ) *
                    </label>
                    <select
                      name="animalId"
                      required
                      className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                      value={formData.animalId}
                      onChange={handleChange}
                    >
                      <option value="">ÿßÿÆÿ™ÿ± ÿßŸÑÿ≠ŸäŸàÿßŸÜ</option>
                      {Array.isArray(selectedCustomer.animals) && selectedCustomer.animals.map(animal => (
                        <option key={animal._id} value={animal._id}>
                          {animal.name} - {animal.type === 'camel' ? 'ÿ•ÿ®ŸÑ' : animal.type === 'sheep' ? 'ÿ£ÿ∫ŸÜÿßŸÖ' : animal.type === 'goat' ? 'ŸÖÿßÿπÿ≤' : animal.type === 'cow' ? 'ŸÖÿßÿ¥Ÿäÿ©' : 'ÿÆŸäŸàŸÑ'}
                        </option>
                      ))}
                    </select>
                  </div>
                ) : selectedCustomer ? (
                  <div className="md:col-span-2 bg-yellow-50 border border-yellow-200 rounded-md p-3">
                    <p className="text-sm text-yellow-800">
                      ‚ö†Ô∏è Ÿáÿ∞ÿß ÿßŸÑÿπŸÖŸäŸÑ ŸÑŸäÿ≥ ŸÑÿØŸäŸá ÿ≠ŸäŸàÿßŸÜÿßÿ™ ŸÖÿ≥ÿ¨ŸÑÿ©. ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ•ÿØÿÆÿßŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ≠ŸäŸàÿßŸÜ ŸäÿØŸàŸäÿßŸã:
                    </p>
                  </div>
                ) : null}

                {(!selectedCustomer || !selectedCustomer.animals || selectedCustomer.animals.length === 0) && formData.customerId && (
                  <>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        ÿßÿ≥ŸÖ ÿßŸÑÿ≠ŸäŸàÿßŸÜ *
                      </label>
                      <input
                        type="text"
                        name="animalName"
                        required
                        placeholder="ŸÖÿ´ÿßŸÑ: ÿ®ÿØÿ±"
                        className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                        value={formData.animalName}
                        onChange={handleChange}
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        ŸÜŸàÿπ ÿßŸÑÿ≠ŸäŸàÿßŸÜ *
                      </label>
                      <select
                        name="animalType"
                        required
                        className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                        value={formData.animalType}
                        onChange={handleChange}
                      >
                        <option value="">ÿßÿÆÿ™ÿ± ÿßŸÑŸÜŸàÿπ</option>
                        <option value="camel">ÿ•ÿ®ŸÑ</option>
                        <option value="sheep">ÿ£ÿ∫ŸÜÿßŸÖ</option>
                        <option value="goat">ŸÖÿßÿπÿ≤</option>
                        <option value="cow">ŸÖÿßÿ¥Ÿäÿ©</option>
                        <option value="horse">ÿÆŸäŸàŸÑ</option>
                      </select>
                    </div>
                  </>
                )}

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    ÿßŸÑÿ™ÿ∑ÿπŸäŸÖ
                  </label>
                  <select
                    name="vaccinationId"
                    required
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    value={formData.vaccinationId}
                    onChange={handleChange}
                  >
                    <option value="">ÿßÿÆÿ™ÿ± ÿßŸÑÿ™ÿ∑ÿπŸäŸÖ</option>
                    {(() => {
                      console.log('Vaccinations in dropdown:', vaccinations);
                      console.log('Is Array?', Array.isArray(vaccinations));
                      console.log('Current animalType:', formData.animalType);
                      
                      if (!Array.isArray(vaccinations)) return null;
                      
                      const filtered = vaccinations.filter(vaccination => {
                        // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ŸáŸÜÿßŸÉ ŸÜŸàÿπ ÿ≠ŸäŸàÿßŸÜ ŸÖÿ≠ÿØÿØÿå ÿßÿπÿ±ÿ∂ ÿßŸÑÿ™ÿ∑ÿπŸäŸÖÿßÿ™ ÿßŸÑŸÖŸÜÿßÿ≥ÿ®ÿ© ŸÅŸÇÿ∑
                        if (formData.animalType) {
                          return vaccination.animalTypes && vaccination.animalTypes.includes(formData.animalType);
                        }
                        // ÿ•ÿ∞ÿß ŸÑŸÖ ŸäŸÉŸÜ ŸáŸÜÿßŸÉ ŸÜŸàÿπ ŸÖÿ≠ÿØÿØÿå ÿßÿπÿ±ÿ∂ ŸÉŸÑ ÿßŸÑÿ™ÿ∑ÿπŸäŸÖÿßÿ™
                        return true;
                      });
                      
                      console.log('Filtered vaccinations:', filtered);
                      
                      return filtered.map(vaccination => (
                        <option key={vaccination._id} value={vaccination._id}>
                          {vaccination.nameAr || vaccination.name} - {vaccination.price} ÿ±ŸäÿßŸÑ
                        </option>
                      ));
                    })()}
                  </select>
                  {formData.animalType && (
                    <p className="text-xs text-gray-500 mt-1">
                      ÿπÿ±ÿ∂ ÿßŸÑÿ™ÿ∑ÿπŸäŸÖÿßÿ™ ÿßŸÑŸÖŸÜÿßÿ≥ÿ®ÿ© ŸÑŸÄ {
                        formData.animalType === 'camel' ? 'ÿßŸÑÿ•ÿ®ŸÑ' :
                        formData.animalType === 'sheep' ? 'ÿßŸÑÿ£ÿ∫ŸÜÿßŸÖ' :
                        formData.animalType === 'goat' ? 'ÿßŸÑŸÖÿßÿπÿ≤' :
                        formData.animalType === 'cow' ? 'ÿßŸÑŸÖÿßÿ¥Ÿäÿ©' :
                        formData.animalType === 'horse' ? 'ÿßŸÑÿÆŸäŸàŸÑ' : 'ÿßŸÑÿ≠ŸäŸàÿßŸÜÿßÿ™'
                      }
                    </p>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    ÿßŸÑŸÅÿ±ÿπ
                  </label>
                  <select
                    name="branchId"
                    required
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    value={formData.branchId}
                    onChange={handleChange}
                  >
                    <option value="">ÿßÿÆÿ™ÿ± ÿßŸÑŸÅÿ±ÿπ</option>
                    {Array.isArray(branches) && branches.map(branch => (
                      <option key={branch._id} value={branch._id}>
                        {branch.name} - {branch.location}
                      </option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑŸÖŸàÿπÿØ
                  </label>
                  <input
                    type="date"
                    name="appointmentDate"
                    required
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    value={formData.appointmentDate}
                    onChange={handleChange}
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    ÿßŸÑŸàŸÇÿ™
                  </label>
                  <select
                    name="timeSlot"
                    required
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    value={formData.timeSlot}
                    onChange={handleChange}
                  >
                    <option value="">ÿßÿÆÿ™ÿ± ÿßŸÑŸàŸÇÿ™</option>
                    <option value="09:00">09:00 ÿµÿ®ÿßÿ≠ÿßŸã</option>
                    <option value="10:00">10:00 ÿµÿ®ÿßÿ≠ÿßŸã</option>
                    <option value="11:00">11:00 ÿµÿ®ÿßÿ≠ÿßŸã</option>
                    <option value="12:00">12:00 ÿ∏Ÿáÿ±ÿßŸã</option>
                    <option value="13:00">01:00 ÿ∏Ÿáÿ±ÿßŸã</option>
                    <option value="14:00">02:00 ÿ∏Ÿáÿ±ÿßŸã</option>
                    <option value="15:00">03:00 ÿπÿµÿ±ÿßŸã</option>
                    <option value="16:00">04:00 ÿπÿµÿ±ÿßŸã</option>
                    <option value="17:00">05:00 ÿπÿµÿ±ÿßŸã</option>
                  </select>
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  ŸÖŸÑÿßÿ≠ÿ∏ÿßÿ™
                </label>
                <textarea
                  name="notes"
                  rows="3"
                  className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  value={formData.notes}
                  onChange={handleChange}
                ></textarea>
              </div>

              <div className="flex justify-end space-x-3 pt-4">
                <button
                  type="button"
                  onClick={() => setShowModal(false)}
                  className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50"
                >
                  ÿ•ŸÑÿ∫ÿßÿ°
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
                >
                  {editingBooking ? 'ÿ™ÿ≠ÿØŸäÿ´' : 'ÿ≠ÿ¨ÿ≤'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Bookings;
