import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Phone, MessageCircle, CreditCard, Users, Sheet, List, BarChart } from 'lucide-react';

const StatsCards = () => {
  const [stats, setStats] = useState([]);
  const [loading, setLoading] = useState(true);

  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000';

  const authorizedFetch = (endpoint, options = {}) => {
    const token = localStorage.getItem('token');
    const headers = {
      ...(options.headers || {}),
    };

    if (token) {
      headers.Authorization = `Bearer ${token}`;
    }

    return fetch(`${API_BASE_URL}${endpoint}`, {
      ...options,
      headers,
    });
  };

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await authorizedFetch('/api/dashboard/stats');
        if (response.ok) {
          const data = await response.json();
          const quickStats = data.data.quickStats;

          const statsData = [
            {
              id: 1,
              title: 'إجمالي العملاء',
              value: quickStats.totalCustomers?.toLocaleString('ar-SA') || '0',
              change: '+12%',
              changeType: 'increase',
              icon: Users,
              color: 'blue',
            },
            {
              id: 2,
              title: 'حجوزات اليوم',
              value: quickStats.todayBookings?.toLocaleString('ar-SA') || '0',
              change: '+8%',
              changeType: 'increase',
              icon: List,
              color: 'green',
            },
            {
              id: 3,
              title: 'استشارات اليوم',
              value: quickStats.todayConsultations?.toLocaleString('ar-SA') || '0',
              change: '+15%',
              changeType: 'increase',
              icon: Phone,
              color: 'purple',
            },
            {
              id: 4,
              title: 'إيرادات اليوم',
              value: `${quickStats.todayRevenue?.toLocaleString('ar-SA') || '0'} ريال`,
              change: '+5%',
              changeType: 'increase',
              icon: CreditCard,
              color: 'orange',
            },
          ];
          setStats(statsData);
        }
      } catch (error) {
        console.error('Error fetching stats:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  const getColorClasses = (color) => {
    const colors = {
      blue: 'from-blue-500 to-blue-600',
      green: 'from-green-500 to-green-600',
      purple: 'from-purple-500 to-purple-600',
      orange: 'from-orange-500 to-orange-600',
    };
    return colors[color] || colors.blue;
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
      {stats.map((stat, index) => {
        const Icon = stat.icon;
        
        return (
          <motion.div
            key={stat.id}
            className="bg-white rounded-2xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow duration-300"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: index * 0.1 }}
            whileHover={{ y: -5 }}
          >
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-600 text-sm font-medium mb-1">
                  {stat.title}
                </p>
                <p className="text-2xl font-bold text-gray-900 mb-2">
                  {stat.value}
                </p>
                <span className="inline-flex items-center text-sm font-medium text-green-600">
                  {stat.change}
                  <span className="mr-1">↗</span>
                </span>
              </div>
              <div className={`p-3 rounded-xl bg-gradient-to-r ${getColorClasses(stat.color)}`}>
                <Icon className="w-6 h-6 text-white" />
              </div>
            </div>
          </motion.div>
        );
      })}
    </div>
  );
};

export default StatsCards;
