import React from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer } from 'recharts';

const segments = [
  { name: 'الإبل', value: 120, color: '#3b82f6' },
  { name: 'الأغنام', value: 70, color: '#60a5fa' },
  { name: 'الماعز', value: 40, color: '#93c5fd' },
];

const total = segments.reduce((s, x) => s + x.value, 0);

// SVG-based label callout attached to each arc
const renderCalloutLabel = (props) => {
  const { cx, cy, midAngle, outerRadius, index } = props;
  const RAD = Math.PI / 180;
  const offset = 18; // distance from outerRadius
  const r = outerRadius + offset;
  const x = cx + r * Math.cos(-midAngle * RAD);
  const y = cy + r * Math.sin(-midAngle * RAD);

  const seg = segments[index];
  const pct = Math.round((seg.value / total) * 100);

  const boxW = 78;
  const boxH = 24;
  const rx = 10;
  const bx = x - boxW / 2;
  const by = y - boxH / 2;

  return (
    <g>
      <rect x={bx} y={by} width={boxW} height={boxH} rx={rx} ry={rx} fill="#fff" stroke="#e5e7eb" />
      {/* dot */}
      <circle cx={bx + boxW - 10} cy={by + boxH / 2} r={3} fill={seg.color} />
      {/* text */}
      <text x={bx + 8} y={by + 14} fill="#374151" fontSize={10}>
        {seg.name.replace(':', '')}: {pct}%
      </text>
    </g>
  );
};

const CenterLabel = ({ left, right, subtitle }) => (
  <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
    <div className="text-center">
      <div className="text-3xl font-extrabold text-gray-900 tracking-tight">{left} / {right}</div>
      <div className="text-xs text-gray-500 mt-1">{subtitle}</div>
    </div>
  </div>
);

const OperationsDistributionChart = () => {
  return (
    <div style={{ width: '100%', height: 320 }} className="relative">
      <ResponsiveContainer>
        <PieChart>
          <Pie
            data={segments}
            innerRadius={68}
            outerRadius={102}
            paddingAngle={2}
            cornerRadius={10}
            startAngle={90}
            endAngle={-270}
            dataKey="value"
            cx="50%"
            cy="50%"
            labelLine={false}
            label={renderCalloutLabel}
          >
            {segments.map((s, i) => (
              <Cell key={s.name} fill={s.color} />
            ))}
          </Pie>
        </PieChart>
      </ResponsiveContainer>

      {/* Center metric */}
      <CenterLabel left={segments[0].value} right={segments[1].value} subtitle="الإبل / الأغنام" />

      {/* Bottom legend */}
      <div className="grid grid-cols-2 gap-4 mt-2 text-sm" dir="rtl">
        <div className="flex items-center justify-between">
          <div>
            <span className="text-gray-700">الإبل:</span>
            <span className="font-semibold mr-1">120 وحدة</span>
          </div>
          <span className="inline-block w-3 h-3 rounded-full" style={{ backgroundColor: segments[0].color }}></span>
        </div>
        <div className="flex items-center justify-between">
          <div>
            <span className="text-gray-700">الأغنام:</span>
            <span className="font-semibold mr-1">70 وحدة</span>
          </div>
          <span className="inline-block w-3 h-3 rounded-full" style={{ backgroundColor: segments[1].color }}></span>
        </div>
        <div className="flex items-center justify-center gap-2 col-span-2">
          <span className="inline-block w-3 h-3 rounded-full" style={{ backgroundColor: segments[2].color }}></span>
          <div className="flex items-center gap-1">
            <span className="text-gray-700">الماعز:</span>
            <span className="font-semibold mr-1">40 وحدة</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OperationsDistributionChart;
