import React from 'react';
import { motion } from 'framer-motion';
import { Phone, MessageCircle, CreditCard, Users } from 'lucide-react';

const StatsCards = () => {
  const stats = [
    {
      id: 1,
      title: 'إجمالي المكالمات',
      value: '1,234',
      change: '+12%',
      changeType: 'increase',
      icon: Phone,
      color: 'blue',
    },
    {
      id: 2,
      title: 'عدد الاستفسارات',
      value: '456',
      change: '+8%',
      changeType: 'increase',
      icon: MessageCircle,
      color: 'green',
    },
    {
      id: 3,
      title: 'عدد المعاملات',
      value: '789',
      change: '+15%',
      changeType: 'increase',
      icon: CreditCard,
      color: 'purple',
    },
    {
      id: 4,
      title: 'إجمالي العملاء',
      value: '2,345',
      change: '+5%',
      changeType: 'increase',
      icon: Users,
      color: 'orange',
    },
  ];

  const getColorClasses = (color) => {
    const colors = {
      blue: 'from-blue-500 to-blue-600',
      green: 'from-green-500 to-green-600',
      purple: 'from-purple-500 to-purple-600',
      orange: 'from-orange-500 to-orange-600',
    };
    return colors[color] || colors.blue;
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
      {stats.map((stat, index) => {
        const Icon = stat.icon;
        
        return (
          <motion.div
            key={stat.id}
            className="bg-white rounded-2xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow duration-300"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: index * 0.1 }}
            whileHover={{ y: -5 }}
          >
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-600 text-sm font-medium mb-1">
                  {stat.title}
                </p>
                <p className="text-2xl font-bold text-gray-900 mb-2">
                  {stat.value}
                </p>
                <span className="inline-flex items-center text-sm font-medium text-green-600">
                  {stat.change}
                  <span className="mr-1">↗</span>
                </span>
              </div>
              <div className={`p-3 rounded-xl bg-gradient-to-r ${getColorClasses(stat.color)}`}>
                <Icon className="w-6 h-6 text-white" />
              </div>
            </div>
          </motion.div>
        );
      })}
    </div>
  );
};

export default StatsCards;
