import React from 'react';
import { motion } from 'framer-motion';
import OperationsChart from './charts/OperationsChart';
import OperationsDistributionChart from './charts/OperationsDistributionChart';
import RevenueChart from './charts/RevenueChart';
import TransactionsChart from './charts/TransactionsChart';

const ChartsGrid = () => {
  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
      {/* Operations Line Chart */}
      <motion.div
        className="bg-white rounded-2xl shadow-sm border border-gray-200 p-6"
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.5, delay: 0.2 }}
      >
        <div className="mb-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-2">
            إجمالي العمليات عبر الوقت
          </h3>
          <p className="text-gray-600 text-sm">
            متابعة الأداء الشهري للعمليات
          </p>
        </div>
        <OperationsChart />
      </motion.div>

      {/* Operations Distribution Pie Chart */}
      <motion.div
        className="bg-white rounded-2xl shadow-sm border border-gray-200 p-6"
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.5, delay: 0.3 }}
      >
        <div className="mb-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-2">
            توزيع العمليات
          </h3>
          <p className="text-gray-600 text-sm">
            نسبة أنواع العمليات المختلفة
          </p>
        </div>
        <OperationsDistributionChart />
      </motion.div>

      {/* Revenue Donut Chart */}
      <motion.div
        className="bg-white rounded-2xl shadow-sm border border-gray-200 p-6"
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.5, delay: 0.4 }}
      >
        <div className="mb-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-2">
            إجمالي الإيرادات
          </h3>
          <p className="text-gray-600 text-sm">
            توزيع مصادر الإيرادات
          </p>
        </div>
        <RevenueChart />
      </motion.div>

      {/* Transactions Bar Chart */}
      <motion.div
        className="bg-white rounded-2xl shadow-sm border border-gray-200 p-6"
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.5, delay: 0.5 }}
      >
        <div className="mb-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-2">
            مقارنة المعاملات
          </h3>
          <p className="text-gray-600 text-sm">
            المعاملات المكتملة مقابل المعلقة
          </p>
        </div>
        <TransactionsChart />
      </motion.div>
    </div>
  );
};

export default ChartsGrid;
