import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import OperationsChart from './charts/OperationsChart';
import OperationsDistributionChart from './charts/OperationsDistributionChart';
import RevenueChart from './charts/RevenueChart';
import TransactionsChart from './charts/TransactionsChart';
import { RefreshCcw, TrendingUp } from 'lucide-react';

const ChartsGrid = () => {
  const [chartsData, setChartsData] = useState(null);
  const [loading, setLoading] = useState(true);

  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000';

  const authorizedFetch = (endpoint, options = {}) => {
    const token = localStorage.getItem('token');
    const headers = {
      ...(options.headers || {}),
    };

    if (token) {
      headers.Authorization = `Bearer ${token}`;
    }

    return fetch(`${API_BASE_URL}${endpoint}`, {
      ...options,
      headers,
    });
  };

  useEffect(() => {
    const fetchChartsData = async () => {
      try {
        const response = await authorizedFetch('/api/dashboard/charts');
        if (response.ok) {
          const data = await response.json();
          setChartsData(data.data);
        }
      } catch (error) {
        console.error('Error fetching charts data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchChartsData();
  }, []);
  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
      {/* Operations Line Chart */}
      <motion.div
        className="bg-white rounded-2xl shadow-sm border border-gray-200 p-6"
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.5, delay: 0.2 }}
      >
        <div className="mb-4 flex items-start justify-between gap-4" dir="rtl">
          <div>
            <h3 className="text-lg font-semibold text-gray-900 mb-2">إجمالي التطعيمات الكلي</h3>
            <div className="flex items-center gap-4 text-sm">
             <div className="text-right">
            <div className='flex justify-center items-center'>
              <div className="text-3xl font-extrabold text-emerald-600 me-4">1023</div>
              <div className="text-xs text-gray-500 mb-1">تطعيم محجوز</div>
             
              
            </div>
            
          </div>
            </div>
          </div>
           <div>
            <span className="flex items-center gap-1 text-blue-600 mb-4">
               آخر تحديث : منذ ساعة
                <RefreshCcw className='mr-1' size={14} />
               
              </span>
              <span className="flex items-center gap-1 text-red-600">
                % 2.45-
                <TrendingUp className='mr-1' size={14} />
                
              </span>
           </div>
        </div>
        <OperationsChart />
      </motion.div>

      {/* Operations Distribution Pie Chart */}
      <motion.div
        className="bg-white rounded-2xl shadow-sm border border-gray-200 p-6"
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.5, delay: 0.3 }}
      >
        <div className="mb-4 flex items-start justify-between gap-4" dir="rtl">
          <div className="text-right">
            <h3 className="text-lg font-semibold text-gray-900 mb-1">نسبة توزيع الحيوانات</h3>
            <div className="text-xs text-gray-500">سبتمبر 2024</div>
          </div>
          <div className="text-left text-sm text-blue-600 flex items-center gap-1">
            آخر تحديث : منذ ساعة
          </div>
        </div>
        <OperationsDistributionChart />
      </motion.div>

      {/* Revenue Donut Chart */}
      <motion.div
        className="bg-white rounded-2xl shadow-sm border border-gray-200 p-6"
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.5, delay: 0.4 }}
      >
        <div className="mb-4 flex items-start justify-between gap-4" dir="rtl">
          <div className="text-right">
            <h3 className="text-lg font-semibold text-gray-900 mb-1">الفروع الأكثر استخدامًا</h3>
            <div className="text-xs text-gray-500">سبتمبر 2024</div>
          </div>
          <div className="text-left text-sm text-blue-600 flex items-center gap-1">
            آخر تحديث : منذ ساعة
          </div>
        </div>
        <RevenueChart />
      </motion.div>

      {/* Transactions Bar Chart */}
      <motion.div
        className="bg-white rounded-2xl shadow-sm border border-gray-200 p-6"
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.5, delay: 0.5 }}
      >
        <div className="mb-4 flex items-start justify-between gap-4" dir="rtl">
          <div className="text-right">
            <h3 className="text-lg font-semibold text-gray-900 mb-1">مقارنة المعاملات</h3>
            <div className="text-xs text-gray-500">سبتمبر 2024</div>
          </div>
          <div className="text-left text-sm text-blue-600 flex items-center gap-1">
            آخر تحديث : منذ ساعة
          </div>
        </div>
        <TransactionsChart />
      </motion.div>
    </div>
  );
};

export default ChartsGrid;
