import React from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

const TransactionsChart = () => {
  const data = [
    {
      month: 'يناير',
      completed: 120,
      pending: 25,
    },
    {
      month: 'فبراير',
      completed: 145,
      pending: 30,
    },
    {
      month: 'مارس',
      completed: 165,
      pending: 20,
    },
    {
      month: 'أبريل',
      completed: 190,
      pending: 15,
    },
    {
      month: 'مايو',
      completed: 175,
      pending: 35,
    },
    {
      month: 'يونيو',
      completed: 200,
      pending: 40,
    },
  ];

  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-white p-3 rounded-lg shadow-lg border border-gray-200">
          <p className="font-medium text-gray-900 mb-2">{label}</p>
          {payload.map((entry, index) => (
            <p key={index} style={{ color: entry.color }}>
              {entry.dataKey === 'completed' ? 'المكتملة' : 'المعلقة'}: {entry.value}
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  return (
    <div style={{ width: '100%', height: 300 }}>
      <ResponsiveContainer>
        <BarChart
          data={data}
          margin={{
            top: 20,
            right: 30,
            left: 20,
            bottom: 5,
          }}
          barCategoryGap="20%"
        >
          <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
          <XAxis 
            dataKey="month" 
            axisLine={false}
            tickLine={false}
            tick={{ fill: '#6b7280', fontSize: 12 }}
          />
          <YAxis 
            axisLine={false}
            tickLine={false}
            tick={{ fill: '#6b7280', fontSize: 12 }}
          />
          <Tooltip content={<CustomTooltip />} />
          <Bar 
            dataKey="completed" 
            fill="#10b981" 
            radius={[4, 4, 0, 0]}
            name="مكتملة"
          />
          <Bar 
            dataKey="pending" 
            fill="#f59e0b" 
            radius={[4, 4, 0, 0]}
            name="معلقة"
          />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
};

export default TransactionsChart;
