import React from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer } from 'recharts';

const segments = [
  { name: 'الإبل', value: 120, color: '#3b82f6' },
  { name: 'الأغنام', value: 70, color: '#60a5fa' },
  { name: 'الماعز', value: 40, color: '#93c5fd' },
];

const total = segments.reduce((s, x) => s + x.value, 0);

const Callout = ({ x, y, text, percent, color, align = 'right' }) => (
  <div
    style={{ position: 'absolute', left: x, top: y, transform: 'translate(-50%, -50%)' }}
    className={`bg-white border border-gray-200 shadow-md rounded-xl px-3 py-1.5 text-xs flex items-center gap-2 ${
      align === 'right' ? 'justify-end' : 'justify-start'
    }`}
  >
    <span className="text-gray-700">{text}</span>
    <span className="font-semibold" style={{ color }}>{percent}%</span>
    <span className="inline-block w-2 h-2 rounded-full" style={{ backgroundColor: color }}></span>
  </div>
);

const CenterLabel = ({ value1, value2 }) => (
  <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
    <div className="text-center">
      <div className="text-2xl font-extrabold text-gray-900">{value1} / {value2}</div>
      <div className="text-xs text-gray-500 mt-1">الإجمالي / الإبل</div>
    </div>
  </div>
);

const OperationsDistributionChart = () => {
  const chartW = 360; // container will scale; used for callout placement estimates
  const chartH = 260;

  const pct = (v) => Math.round((v / total) * 100);

  return (
    <div style={{ width: '100%', height: 320 }} className="relative">
      <ResponsiveContainer>
        <PieChart>
          <Pie
            data={segments}
            innerRadius={60}
            outerRadius={90}
            paddingAngle={2}
            cornerRadius={10}
            startAngle={90}
            endAngle={-270}
            dataKey="value"
            cx="50%"
            cy="50%"
          >
            {segments.map((s, i) => (
              <Cell key={s.name} fill={s.color} />
            ))}
          </Pie>
        </PieChart>
      </ResponsiveContainer>

      {/* Center metric */}
  {/* Center shows 120 / 70 as in the design */}
  <CenterLabel value1={120} value2={70} />

      {/* Floating callouts (approximate positions) */}
  <Callout x={chartW * 0.73} y={chartH * 0.22} text="الإبل:" percent={pct(segments[0].value)} color={segments[0].color} />
  <Callout x={chartW * 0.32} y={chartH * 0.35} text="الأغنام:" percent={pct(segments[1].value)} color={segments[1].color} />
  <Callout x={chartW * 0.18} y={chartH * 0.62} text="الماعز:" percent={pct(segments[2].value)} color={segments[2].color} />

      {/* Bottom legend */}
      <div className="grid grid-cols-2 gap-4 mt-2 text-sm" dir="rtl">
        <div className="flex items-center justify-between bg-white">
          <div>
            <span className="text-gray-700">الإبل:</span>
            <span className="font-semibold mr-1">120 وحدة</span>
          </div>
          <span className="inline-block w-3 h-3 rounded-full" style={{ backgroundColor: segments[0].color }}></span>
        </div>
        <div className="flex items-center justify-between bg-white">
          <div>
            <span className="text-gray-700">الأغنام:</span>
            <span className="font-semibold mr-1">70 وحدة</span>
          </div>
          <span className="inline-block w-3 h-3 rounded-full" style={{ backgroundColor: segments[1].color }}></span>
        </div>
        <div className="flex items-center bg-white col-span-2">
          <div>
            <span className="text-gray-700">الماعز:</span>
            <span className="font-semibold mr-1">40 وحدة</span>
          </div>
          <span className="inline-block w-3 h-3 rounded-full mr-2" style={{ backgroundColor: segments[2].color }}></span>
        </div>
      </div>
    </div>
  );
};

export default OperationsDistributionChart;
